/* Loads localForage 1.7.3 from jsDelivr if not already present. */
(function(){
  if (window.localforage) return;
  var s = document.createElement('script');
  s.src = "https://cdn.jsdelivr.net/npm/localforage@1.7.3/dist/localforage.min.js";
  s.onload = function(){ console.log("localforage loaded"); };
  s.onerror = function(){ console.warn("Failed to load localforage"); };
  document.head.appendChild(s);
})();